using Grpc.Core;
using Grpc.Core.Interceptors;
using Grpc.Net.Client;
using GrpcGreeter;
using System;
using System.Reflection;
using System.Threading;
using System.Threading.Tasks;

namespace GrpcClinetConsoleApp
{
	/// <summary>
	/// https://docs.microsoft.com/ja-jp/aspnet/core/grpc/client?view=aspnetcore-3.1
	/// 
	/// https://docs.microsoft.com/ja-jp/aspnet/core/grpc/basics?view=aspnetcore-3.1#generated-c-assets
	/// </summary>
	class Program
	{
		static async Task Main(string[] args)
		{
			var tid = Environment.TickCount % 1000;

			Console.WriteLine("Grpc Client Start!");
			var channel = GrpcChannel.ForAddress("https://localhost:5001");
			var client = new Greeter.GreeterClient(channel);

		start:
			var start = Environment.TickCount;
			try
			{
				HelloReply response = null;
				const int MAX = 100;
				var messageHeader = $"World 端末ID:{tid:D3}, クライアント送信時間:{DateTime.Now.ToString("HH:mm:ss.FFF")}";
				for (int c = 0; c < MAX; c++)
				{
					response = await client.SayHelloAsync(
					new HelloRequest { Name =  messageHeader + "" },
					new CallOptions(deadline: DateTime.UtcNow.AddSeconds(10)));

					//response = await client.SayHelloAsync(
					//new HelloRequest { Name = $"World tid:{tid:D3}, cl time:{DateTime.Now.ToString("HH:mm:ss.FFF")}" });

				}
				Console.WriteLine($"メッセージヘッダー={messageHeader}, {MAX}件の送信時間={Environment.TickCount - start:N0}ms");
			}
			catch (Exception ex)
			{
				Console.WriteLine(ex.ToString());
				throw new SystemException($"メッセージの送信でエラーが発生しました。, 処理時間={Environment.TickCount - start:N0}ms", ex);
			}
			//
			Random random = new Random(Environment.TickCount);
			var sleep = random.Next(100, 5000);
			Console.Write($"Sleep({sleep}); Start・・");
			Thread.Sleep(sleep);
			Console.WriteLine($"End");

			//var key = Console.ReadKey();
			//if (key.Key == ConsoleKey.R ) 
			goto start;
		}
	}
}
